mapper.select:
select u.id as id, u.login as login, u.name as name, ur.role as role from users u
left join users_roles ur on ur.user_id = u.id
where u.id = #{id}

select u.id as id, u.login as login, u.password as password, u.name as name, ur.role as role from users u
left join users_roles ur on ur.user_id = u.id
where u.login = #{name}
