mapper.select:
select f.id as id, f.name as name, s.id as s_id, s.name as s_name
from functions f join specializations s on s.id = f.specialization_id
where f.id = #{id}

select f.id as id, f.name as name, s.id as s_id, s.name as s_name
from functions f join specializations s on s.id = f.specialization_id
where f.specialization_id = #{id}
